package Lecture4;

public class Practice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 14;
		if( n % 2 == 0) {
			System.out.println(n +" is even");
		} else {
			System.out.println(n +" is odd");
		}
		
		int m = 17;
		if( m % 2 == 0) {
			System.out.println(m +" is even");
		} else {
			System.out.println(m +" is odd");
		}
	}
	
	

}
